import React from "react";

export default function Breadcrumb(props = false, category = false) {
    const { product } = props;

    return (

        <div className="breadcrumb">
            HOME <i className="fa fa-chevron-right" />
            {category ? (
                <>{category} {!justCategory && <i className="fa fa-chevron-right" />}</>
            ) : (
                <>{product.category} {!justCategory && <i className="fa fa-chevron-right" />} {product.title}</>
            )}
        </div>
    )
}
