import React, { useContext, useState } from "react";
import { TheContext } from "../comps/functions/TheContext";
import Breadcrumb from "../comps/Breadcrumb";
import all_product from "../comps/assets/all_product";
import { Link } from "react-router-dom";
import Message from "../comps/Message";

export default function Cart() {
    const { addToCart, cartItems, remvoeFromCart } = useContext(TheContext);
    const [message, setMessage] = useState("");

    const totalPrice = Object.values(cartItems).reduce((acc, val) => {
        const product = all_product.find((e) => e.id === val.id);
        return acc + product.price * val.count;
    }, 0);

    const handleDecrement = (id) => {
        if (cartItems[id] > 0) {
            remvoeFromCart(id);
        }
    };

    const handleIncrement = (id) => {
        const product = all_product.find((e) => e.id === id);
        if (cartItems[id] < product.quantity) {
            addToCart(id);
        } else if (message) {
            return;
        } else {
            setMessage("Maximum number reached!");
            setTimeout(() => {
                setMessage("");
            }, 3000);
        }
    };

    return (
        <section className="cart py-5"><Message message={message} show={message.length > 0} />
            <Breadcrumb cart />
            <div className="d-flex align-items-center justify-content-between">
                <h1>Shopping Cart: </h1>
                <span>Price</span>
            </div>
            <hr className="mt-0" />
            {all_product.map((e) => {
                if (cartItems[e.id]) {
                    return (
                        <div className="d-flex justify-content-between" key={e.id}>
                            <div className="card-cart">
                                <Link to={`/${e.category}/Product/${e.id}`} onClick={window.scrollTo(0, 0)}>
                                    <img src={e.cover} alt={e.title} />
                                    <h2>{e.title}</h2>
                                </Link>
                                <span>{e.price}</span>
                                <span>{e.ssize}</span>
                                <span>{e.scolor}</span>
                                <span>{e.brand}</span>
                                <div className="addToCart">
                                    <button className="button button-classic" onClick={() => handleDecrement(e.id)}>-</button>
                                    <span className="flexCentralizer">{cartItems[e.id].count}</span>
                                    <button className="button button-classic" onClick={() => handleIncrement(e.id)}>+</button>
                                </div>
                            </div>
                            <button>
                                <i className="fal fa-xmark" />
                            </button>
                        </div>
                    );
                }
            })}
            <hr />
            <div className="d-flex justify-content-end">
                <span>Total Price : {totalPrice}</span>
            </div>
        </section>
    );
}
