import { useEffect } from 'react';

const scrollToTop = () => {
    useEffect(() => {
        const handleScroll = () => {
            window.scrollTo(0, 0);
        };

        window.addEventListener('popstate', handleScroll);

        return () => {
            window.removeEventListener('popstate', handleScroll);
        };
    }, []);
};

export default scrollToTop;
