import React, { useContext, useState } from "react";
import { TheContext } from "../comps/functions/TheContext";
import Breadcrumb from "../comps/Breadcrumb";
import all_product from "../comps/assets/all_product";
import { Link } from "react-router-dom";
import Message from "../comps/Message";

export default function Cart() {
    const { addToCart, cartItems, remvoeFromCart } = useContext(TheContext);
    const [message, setMessage] = useState("");

    const handleDecrement = (e) => {
        if (cartItems[e.id] > 0) {
            addToCart(e.id, cartItems[e.id] - 1);
        }
    };

    const handleIncrement = (e) => {
        if (cartItems[e.id] < e.quantity) {
            addToCart(e.id, cartItems[e.id] + 1);
        } else if (message) {
            return;
        } else {
            setMessage("Maximum number reached!");
            setTimeout(() => {
                setMessage("");
            }, 3000);
        }
    };

    const totalPrice = Object.values(cartItems).reduce((total, count) => {
        const product = all_product.find((p) => p.id === count.id);
        return total + count * product.price;
    }, 0);

    return (
        <section className="cart py-5">
            <Message message={message} show={message.length > 0} />
            <Breadcrumb cart />
            <div className="d-flex align-items-center justify-content-between">
                <h1>Shopping Cart: </h1>
                <span>Price</span>
            </div>
            <hr className="mt-0" />
            {Object.values(cartItems).map((count, index) => {
                const e = all_product[index];
                if (count > 0) {
                    return (
                        <div className="d-flex justify-content-between" key={e.id}>
                            <div className="card-cart">
                                <Link to={`/${e.category}/Product/${e.id}`} onClick={window.scrollTo(0, 0)}>
                                    <img src={e.cover} alt={e.title} />
                                    <h2>{e.title}</h2>
                                </Link>
                                <span>{e.price}</span>
                                <span>{e.selectedSize}</span>
                                <span>{e.selectedColor}</span>
                                <span>{e.brand}</span>
                                <div className="addToCart">
                                    <button className="button button-classic" onClick={() => handleDecrement(e)}>-</button>
                                    <span className="flexCentralizer">{count}</span>
                                    <button className="button button-classic" onClick={() => handleIncrement(e)}>+</button>
                                </div>
                            </div>
                            <button onClick={() => remvoeFromCart(e.id)}>
                                <i className="fal fa-xmark" />
                            </button>
                        </div>
                    );
                }
            })}
            <hr />
            <div className="d-flex justify-content-end">
                <span>Total Price : {totalPrice}</span>
            </div>
        </section >
    );
}
