import React, { useContext } from "react";
import Products from "../comps/functions/Products";
import TheContext from "./functions/TheContext";

export default function QaraProducts() {

    const contextValue = useContext(TheContext);

    if (!contextValue.allProduct) {
        return <div>Loading...</div>;
    }

    const { allProduct } = contextValue;

    return (
        <section className="py-5">
            <h3>. Qara Exclusive</h3>
            <hr />
            {allProduct.map((item, i) => {
                if (item.brand === "qara") {
                    return (
                        <p>{item.title}</p>
                    )
                } else {
                    <h1>nothing</h1>
                }
            })}
            <hr />
        </section>
    );
}



