import React, { useState, useEffect, createContext } from "react";
import all_product from "../assets/all_product";

export const TheContext = createContext({
    all_product,
    cartItems: {},
    addToCart: () => { },
    removeFromCart: () => { },
    increasProduct: () => { },
    dicreasProduct: () => { },
});

const TheContextProvider = (props) => {
    const [cartItems, setCartItems] = useState(getDefaultCart());

    const addToCart = (productID, count, selectedSize, selectedColor) => {
        setCartItems((prev) => ({
            ...prev,
            [productID]: {
                ...prev[productID],
                count: (prev[productID] && prev[productID].count) || 0 + count,
                selectedSize,
                selectedColor,
            },
        }));
    };

    const removeFromCart = (productID) => {
        setCartItems((prev) => {
            const newCartItems = { ...prev };
            delete newCartItems[productID];
            return newCartItems;
        });
    };

    const increasProduct = (productID) => {
        setCartItems((prev) => ({
            ...prev,
            [productID]: {
                ...prev[productID],
                count: prev[productID].count + 1,
            },
        }));
    };

    const dicreasProduct = (productID) => {
        setCartItems((prev) => ({
            ...prev,
            [productID]: {
                ...prev[productID],
                count: prev[productID].count - 1,
            },
        }));
    };

    useEffect(() => {
        console.log(cartItems);
    }, [cartItems]);

    const contextValue = {
        all_product,
        cartItems,
        addToCart,
        removeFromCart,
        increasProduct,
        dicreasProduct,
    };

    return (
        <TheContext.Provider value={contextValue}>
            {props.children}
        </TheContext.Provider>
    );
};

const getDefaultCart = () => {
    let cart = {};
    for (let i = 0; i < all_product.length + 1; i++) {
        cart[i] = {
            count: 0,
            selectedSize: null,
            selectedColor: null,
        };
    }
    return cart;
};

export default TheContextProvider;
