import React from "react";
import Data from "./assets/all_product"
import Card from "./Card";

export default function Popular() {

    function populars(gender) {
        const products = Data.filter(item => item.category === gender);
        const lastFour = womenProducts.slice(-4);

        return (
            lastFour.map((item, index) => {
                return <Card key={index} title={item.title} cover={item.cover} price={item.price} date={item.date} />
            })
        )
    }

    return (
        <>
            <section className="py-5">
                <h3>. POPULAR BY WOMEN</h3>
                <hr />
                <div className="row gy-3">
                    {populars("women")}
                </div>
            </section>
            <section className="py-5">
                <h3>. POPULAR BY WOMEN</h3>
                <hr />
                <div className="row gy-3">
                    {populars("women")}
                </div>
            </section>
        </>
    );
}
