import React from "react";

export default function Breadcrumb({ product = {}, category = false } = {}) {
    return (
        <div className="breadcrumb d-flex align-items-center">
            HOME <i className="fa fa-chevron-right px-1" />
            {category ? (
                category
            ) : (
                <>
                    {product.category} <i className="fa fa-chevron-right px-1" /> {product.title}
                </>
            )}
        </div>
    );
}
