import React, { useContext } from "react";
import { TheContext } from "../comps/functions/TheContext";
import Breadcrumb from "../comps/Breadcrumb";
import all_product from "../comps/assets/all_product";

export default function Cart() {

    const { addToCart, cartItems, remvoeFromCart } = useContext(TheContext);
    const totalPrice = 0;

    return (
        <section className="cart py-5">
            <Breadcrumb cart />
            <div className="d-flex align-items-center justify-content-between">
                <h1>Shopping Cart: </h1>
                <span>Price</span>
            </div>
            <hr className="mt-0" />
            {all_product.map((e) => {
                if (cartItems[e.id] > 0) {
                    <div className="card-cart">
                        <img src={e.cover} alt={e.title} />
                        <h2>{e.title}</h2>
                        <span>{e.ssize}</span>
                        <span>{e.scolor}</span>
                    </div>
                }
            })}
            <hr />
            <div className="d-flex justify-content-end">
                <span>Total Price : {totalPrice}</span>
            </div>
        </section>
    );
}
