import React, { useState, useEffect, createContext } from "react";
import all_product from "../assets/all_product";

export const TheContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    for (let i = 0; i < all_product.length + 1; i++) {
        cart[i] = 0
    }
    return cart;
}

const TheContextProvider = (props) => {

    const [cartItems, setCartItems] = useState(() => {
        const storedCart = localStorage.getItem('cart');
        return storedCart ? JSON.parse(storedCart) : getDefaultCart();
    });

    const addToCart = (productID, count, selectedSize, selectedColor) => {
        setCartItems((prev) => {
            const updatedCart = {
                ...prev,
                [productID]: {
                    ...prev[productID],
                    count,
                    selectedSize,
                    selectedColor,
                },
            };
            localStorage.setItem('cart', JSON.stringify(updatedCart));
            return updatedCart;
        });
    };

    const removeFromCart = (productID) => {
        setCartItems((prev) => {
            const updatedCart = { ...prev, [productID]: 0 };
            localStorage.setItem('cart', JSON.stringify(updatedCart));
            return updatedCart;
        });
    }

    const increasProduct = (productID) => {
        setCartItems((prev) => {
            const updatedCart = { ...prev, [productID]: { ...prev[productID], count: prev[productID].count + 1 } };
            localStorage.setItem('cart', JSON.stringify(updatedCart));
            return updatedCart;
        });
    }

    const dicreasProduct = (productID) => {
        setCartItems((prev) => {
            const updatedCart = { ...prev, [productID]: { ...prev[productID], count: prev[productID].count - 1 } };
            localStorage.setItem('cart', JSON.stringify(updatedCart));
            return updatedCart;
        });
    }

    useEffect(() => {
        console.log(cartItems);
    }, [cartItems]);

    const contextValue = {
        all_product,
        cartItems,
        addToCart,
        removeFromCart,
        increasProduct,
        dicreasProduct,
    };

    return (
        <TheContext.Provider value={contextValue}>
            {props.children}
        </TheContext.Provider>
    )
}


export default TheContextProvider;
