import React, { useState, useEffect } from "react";
import { createContext } from "react";
import produce from "immer";
import all_product from "../assets/all_product"

export const TheContext = createContext(null);

const getDefaultCart = () => {
    let cart = {};
    for (let i = 0; i < all_product.length + 1; i++) {
        cart[i] = {
            count: 0,
            selectedSize: "",
            selectedColor: "",
        }
    }
    return cart
}

const TheContextProvider = (props) => {

    const [cartItems, setCartItems] = useState(getDefaultCart());

    const addToCart = (productID, count, selectedSize, selectedColor) => {
        setCartItems(produce((draft) => {
            draft[productID] = {
                ...draft[productID],
                count,
                selectedSize,
                selectedColor,
            }
        }));
    };

    const removeFromCart = (productID) => {
        setCartItems(produce((draft) => {
            draft[productID] = {
                ...draft[productID],
                count: 0,
            }
        }));
    }

    const increasProduct = (productID) => {
        setCartItems(produce((draft) => {
            draft[productID].count += 1;
        }));
    }
    const dicreasProduct = (productID) => {
        setCartItems(produce((draft) => {
            draft[productID].count -= 1;
        }));
    }

    useEffect(() => {
        console.log(cartItems);
    }, [cartItems]);

    const contextValue = {
        all_product,
        cartItems,
        addToCart,
        removeFromCart,
        increasProduct,
        dicreasProduct,
    };

    return (
        <TheContext.Provider value={contextValue}>
            {props.children}
        </TheContext.Provider>
    )
}


export default TheContextProvider;
