import React, { useContext, useState } from "react";
import { TheContext } from "../comps/functions/TheContext";
import Breadcrumb from "../comps/Breadcrumb";
import all_product from "../comps/assets/all_product";
import { Link } from "react-router-dom";
import Message from "../comps/Message"

export default function Cart() {

    const { addToCart, cartItems, remvoeFromCart } = useContext(TheContext);
    const totalPrice = 0;
    const [count, setCount] = useState(cartItems);
    const [message, setMessage] = useState("");

    const handleDecrement = (product) => {
        if (count > 0) {
            setCount(count - 1);
        }

    };

    const handleIncrement = (product) => {
        if (count < product.quantity) {
            setCount(count + 1);
        } else if (message) {
            return
        } else {
            setMessage("Maximum number reached!");
            setTimeout(() => {
                setMessage("");
            }, 3000);
        }
    };


    return (
        <section className="cart py-5">
            <Message message={message} show={message.length > 0} />
            <Breadcrumb cart />
            <div className="d-flex align-items-center justify-content-between">
                <h1>Shopping Cart: </h1>
                <span>Price</span>
            </div>
            <hr className="mt-0" />
            {all_product.map((product, index) => {
                if (cartItems[product.id] > 0) {
                    <div className="d-flex justify-content-between" key={index}>
                        <div className="card-cart">
                            <Link to={`/${product.category}/Product/${product.id}`} onClick={window.scrollTo(0, 0)}>
                                <img src={product.cover} alt={product.title} />
                                <h2>{product.title}</h2>
                            </Link>
                            <span>{product.price}</span>
                            <span>{product.selectedSize}</span>
                            <span>{product.selectedColor}</span>
                            <span>{product.brand}</span>
                            <div className="addToCart">
                                <button className="button button-classic" onClick={handleDecrement(cartItems[product.id])}>-</button>
                                <span className="flexCentralizer">{count}</span>
                                <button className="button button-classic" onClick={handleIncrement(cartItems[product.id])}>+</button>
                            </div>
                        </div>
                        <button>
                            <i className="fal fa-xmark" />
                        </button>
                    </div>
                }
            })}
            <hr />
            <div className="d-flex justify-content-end">
                <span>Total Price : {totalPrice}</span>
            </div>
        </section >
    );
}
